sys_ui_action.script:
(function() {
	// This does not work due to a defect in X that will be fixed in Y.  If you try to call a module cross scope you get the error `"require" is not defined.`.  Will re-enable this when fixed in Yokohoma.
	if(current.sys_scope != gs.getCurrentApplicationId()){
		gs.addInfoMessage("Calling Wrap in Script Include from a scope that differs from the module.  Attempting to insert the wrapper into the same scope as this module.");
	}
    const { path } = current;
    const { scope } = current.sys_scope;

    const strip_scope_prefix_regex = new RegExp(`^(${scope}\.)`);

    const name = to_snake_case(path.replace(strip_scope_prefix_regex, ""));
	if(path.length > 100){
		gs.addErrorMessage("The generated name is longer than the 100 character limit for the script include name and api_name fields and thus was truncated.  Please verify and update if necessary.");
	}
    var script = `var ${name} = Class.create();
${name}.prototype = (function() {
    var x = require('${path}');

    x.initialize = function() {};
    x.type = '${name}';
    return x;
})();`;

	var gr = new GlideRecord("sys_script_include");
	gr.initialize();
	gr.name = name;
	gr.description = `This script include was created to expose all exported elements of the EcmaScript Module ${path} for use cross-scope.`;
	gr.script = script;
	gr.sys_scope = current.sys_scope;
	gr.access = "public";
	gr.insert();

    gs.addInfoMessage(`Created new script named ${name} (${api_name}) \n ${script}`);

	action.setRedirectUrl(gr);

    function to_snake_case(str) {
        return str && str.match(
                /[A-Z]{2,}(?=[A-Z][a-z]+[0-9]*|\b)|[A-Z]?[a-z]+[0-9]*|[A-Z]|[0-9]+/g)
            .map(s => s.toLowerCase())
            .join('_');
    }
})();
